catalog_script_client.script:
function onSubmit() {

    var req_type = g_form.getValue('request_type');
    var esd_split = g_form.getValue('add_esd_split');
    var new_esd_split = g_form.getValue('add_new_esd_split');
    var a = g_form.getValue('percentage_esd_1');
    var b = g_form.getValue('percentage_esd_2');
    var c = g_form.getValue('percentage_esd_3');

    var aa = parseInt(a);
    var ab = parseInt(a) + parseInt(b);
    var abc = parseInt(a) + parseInt(b) + parseInt(c);

    if ((req_type == 'Accenture ESD Change Request' || req_type == 'Avanade ESD Change Request') && esd_split == 'false' && new_esd_split == 'false') {
        if ((aa < 100) || (aa > 100)) {
            g_form.addErrorMessage('% ESD value should not be more or less than 100%');
            return false;
        }
    }
    if ((req_type == 'Accenture ESD Change Request' || req_type == 'Avanade ESD Change Request') && esd_split == 'true' && new_esd_split == 'false') {
        if ((ab < 100) || (ab > 100)) {
            g_form.addErrorMessage('% ESD and % ESD 2 value should not be more or less than 100%');
            return false;
        }
    }
    if ((req_type == 'Accenture ESD Change Request' || req_type == 'Avanade ESD Change Request') && esd_split == 'true' && new_esd_split == 'true') {
        if ((abc < 100) || (abc > 100)) {
            g_form.addErrorMessage('% ESD, % ESD 2 and % ESD 3 value should not be more or less than 100%');
            return false;
        }
    }

}
