sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
   //Type appropriate comment here, and begin script below
   	var grossSales= g_form.getValue('gross_sales_in_local_currency');
    var cogsTotal = newValue;

    var total = Number(grossSales) - Number(cogsTotal);   //make certain they're numbers before summing.
    var percentMar = Number(total) / Number(grossSales);
	var percentMarFinal = Number (percentMar)*100;

   if(grossSales!='') {
   g_form.setValue('margin_local_currency', total);
   g_form.setValue('margin_percentage', percentMarFinal);
   }
}
