sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Perform a GlideAjax call to the server-side script
    var ga = new GlideAjax('hide_fiscal_month_records'); // CustomServerScript is the name of the server-side script
    ga.addParam('sysparm_name', 'hideRecords');
    ga.addParam('sysparm_new_value', newValue);
    ga.getXML(handleResponse);
}

function handleResponse(response) {
    var answer = response.responseXML.documentElement.getAttribute('answer');

    if (answer === 'success') {
        // Hide or remove the records from the reference table as needed
        // You might need to use DOM manipulation or GlideList API here
    }
}
