sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
   //Type appropriate comment here, and begin script below
	var manu1 = g_form.getValue('cogs_local_ccy_manufactuer_1');  //assuming this is the script for Tuition1, we want the newest value in the field.
    var manu2= g_form.getValue('cogs_local_ccy_manufactuer_2');
    var manu3 =  newValue;
	var manu4 = g_form.getValue('cogs_local_ccy_manufactuer_4');
	var manu5 = g_form.getValue('cogs_local_ccy_manufactuer_5');



    var total = Number(manu1) + Number(manu2) + Number(manu3) + Number(manu4) + Number(manu5);   //make certain they're numbers before summing.


   g_form.setValue('cost_of_good_sold', total);
}
