sys_script_include.script:
var hide_fiscal_month_records = Class.create();
hide_fiscal_month_records.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    initialize: function() {},

    hideRecords: function(new_fiscal_period) {
        var answer = 'success';

        // Check if the new fiscal period is valid
        if (!new_fiscal_period) {
            return 'failure';
        }

        // Create a GlideRecord query to find records in the reference table
        var gr = new GlideRecord('x_amspe_au_finan_0_reporting_period'); // Replace with the actual table name
        gr.addQuery('fiscal_period', new_fiscal_period); // Replace 'fiscal_period_field' with the actual field name for fiscal period
        gr.query();

        // Loop through the matching records and update them
        while (gr.next()) {
			var period = gr.getValue('fiscal_period');

            gr.setValue('fiscal_month', true); // Replace 'fiscal_month_hidden' with the field you want to use for hiding
            gr.update();
        }

        return answer;
    },
    type: 'hide_fiscal_month_records'
});

